print("Enter word limit: ")
wordLimit = int(input())


ArtistObj = []


class Artist:
	def __init__(self, name, songs, wordcount, uniquewords, tally):
	    self.name = name
	    self.songs = songs
	    self.wordcount = wordcount
	    self.uniquewords = uniquewords
	    self.wordtally = tally



def scrape_song(genius_url):
	lyrics_url = requests.get(genius_url)
	soup = BeautifulSoup(lyrics_url.content, 'html.parser')
	words = []
	for div in soup.findAll('div', attrs = {'class': 'lyrics'}):
		asdf = div.get_text()
		asdf = asdf.replace('\n', ' ')
		asdf = re.sub(r'\[.*?\]', "", asdf)
		lyrics_pre_clean = asdf.strip().split(" ")
		
		for x in lyrics_pre_clean:
			if len(x) > 0 and "[" not in x and "]" not in x and "Intro" not in x:
	 			words.append(x.lower().replace(",", "").replace(".", "").replace("/", "").replace("(", "").replace(")", "").replace("\u2005", " ").replace("\u0119", " ").replace("\u200a", " ").replace('"', "").replace("\u015f", "s").replace("\u0307", "").replace("\U0001f985", "").replace("\u2028", " ").replace('\u0103', 'a').replace("\u0219", "s").replace('\u021b', "t"))
	return words

def getSongsFromText(songlist, data):
	global allLyrics
	lyricslist = []
	for song in songlist:
		lyricslist.append(scrape_song(song[1]))
	allLyrics = allLyrics + lyricslist
	return lyricslist


def createArtistObjects(input_list, data):
	for artist in input_list:
		songs = getSongsFromText(data[artist], data)
		songdata = countWords(songs)
		wordcount = len(songdata[0])
		uniquewords = len(songdata[1])
		wordTally = songdata[2]
		artist = Artist(artist, songs, wordcount, uniquewords, wordTally)
		ArtistObj.append(artist)


def countWords(songs):
	createsample = []
	for song in songs:
		createsample = createsample + song
	limitedsample = createsample[0:wordLimit]
	uniquewordsinlimitedsample = list(set(limitedsample))
	tally = wordTally(limitedsample)
	return [limitedsample, uniquewordsinlimitedsample, tally]

def wordTally(x):
	tallyDict = {}
	for word in x:
		isitthere = tallyDict.get(word, False)
		if isitthere == False:
			tallyDict[word] = 1
		else:
			tallyDict[word] +=1
	return tallyDict


def createTotalTally(x):
	bork = []
	for item in x:
		bork = bork + item
	this = wordTally(bork)
	return this
	
createArtistObjects(input_list, data)
totaltoprint = createTotalTally(allLyrics)




